static void get_property_impl(
    GObject *object,
    guint property_id,
    GValue *value,
    GParamSpec *pspec
)
{
    TestAnimal *animal = TEST_ANIMAL(object);
    TestAnimalPrivate *priv = test_animal_get_instance_private(animal);

    switch (property_id) {
    case PROP_ANIMAL_NAME:
        // æ‰€ä»¥ä½ çœ‹å±æ€§è¿™ä¸ªåç§°å’Œæˆ‘ä»¬æƒ³çš„å±æ€§ä¸ä¸€æ ·ï¼Œ
